import React, { useState, useRef, useCallback, useMemo } from "react";
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';

type Props = {
};
const items = [1, 2, 3, 4, 5];

const RoomSlide: React.FunctionComponent<Props> = ({
}) => {
    const galleryItems = useMemo(() => items.map((i) => <h2 key={i}>{i}</h2>), []);
    let carouselRef: any = useRef();
    const handleOnDragStart = (e: { preventDefault: () => any; }) => e.preventDefault()
    return (
        <div>
            <AliceCarousel
                dotsDisabled={true}
                buttonsDisabled={true}
                items={galleryItems}
                ref={(el) => (carouselRef = el)}
            />
            <nav>{items.map((item, i) => (
                    <span key={item} onClick={() => carouselRef.slideTo(i)}>
                        *{' '}
                    </span>
                ))}
            </nav>
            <button onClick={() => carouselRef.slidePrev()}>Prev button</button>
            <button onClick={() => carouselRef.slideNext()}>Next button</button>
        </div>
    );
}

export default RoomSlide;
