import React, { useState, useRef, useCallback } from "react";
import ReactSwipe from 'react-swipe';
import { Room } from "../generated/graphql";
import '../assets/scss/room.scss';
import { url } from "inspector";
type Props = {
    room: Room
};

const RoomdetailPicture: React.FunctionComponent<Props> = ({
    room
}) => {
    let carouselRef: any = useRef();
    return (
        <div style={ { width: '100%', height: '100%' } }>
            <ReactSwipe
                className="carousel"
                swipeOptions={{ continuous: true }}
                ref={el => (carouselRef = el)}
            >
                <div style={ { width: '100%', height: '100%' } }>
                    <div style={ { width: '100%', height: '100%', backgroundImage: 'url("http://imgnews.naver.net/image/5667/2019/07/08/0000004629_001_20190709125104196.png")' } }></div>
                </div>
                <div style={ { width: '100%', height: '100%' } }>
                   <div style={ { width: '100%', height: '100%', backgroundImage: 'url("http://post.phinf.naver.net/MjAxNzA4MDJfMjEy/MDAxNTAxNjAxNzcwNDEz.o_TRfvx1Lb5jaehAVZNMs3NCjL_DB3CmaA_mzxN6QJ4g.DivsPXGnEvHHMwvoK8E73AnBb1kOLz71rK4gZm1cn8Mg.JPEG/IrsAll7oTaSlD7V4LXa7_eVQdy8o.jpg")' } }></div>
                </div>
            </ReactSwipe>
            <button onClick={() => carouselRef.next()}>Next</button>
            <button onClick={() => carouselRef.prev()}>Previous</button>
        </div>
    );
}

export default RoomdetailPicture;
