import React, { useState, useRef, useCallback } from "react"
import { Room } from "../generated/graphql";
import 'antd/dist/antd.css';
import { Carousel, Icon } from 'antd';
import '../assets/scss/room.scss';
import Router from 'next/router'
type Props = {
    room: Room
};

const RoomdetailPicture: React.FunctionComponent<Props> = ({
    room
}) => {
    const carouselRef: any = useRef();
    return (
        <div
            className={`w-100 mb-10 px-2 overflow-hidden`}
            style={{ position: 'relative' }}
        >
            <Carousel
                ref={carouselRef}
                autoplay={true}
            >
                {
                    room?.photoConnection.map((photo, index) => (
                        <div key={index}>
                            <div className="w-full h-64 bg-cover bg-center rounded-lg mb-2 " style={{ backgroundImage: `url("${room.photoConnection[index].file}")` }}></div>
                        </div>
                    ))
                }
            </Carousel>
        </div>
    );
}

export default RoomdetailPicture;
