import React, { useState, useRef, useCallback, useMemo } from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import { useSelectTopRoomsQuery, Room } from "../generated/graphql";
import RoomCard from "./RoomCard";
import { Icon } from "antd";
import _ from "lodash";

type Props = {
    room: Room
};

const RoomImages: React.FunctionComponent<Props> = ({
    room
}) => {
    const [brightList, setBrightList] = useState([1, 2, 3, 4, 5]);

    return (
        <div className="roomimages-container1" onMouseLeave={() => setBrightList([1, 2, 3, 4, 5])}>
            <div className="roomimages-container2_2" style={ { display: `${_.includes(brightList, 1) ? 'inline' : 'none'}` } } onMouseOver={() => setBrightList([1])}/>
            <div className="roomimages-container2" style={{ backgroundImage: `url("${room?.photoConnection[0]?.file}")` }} />
            <div className="roomimages-container2">
                <div className="roomimages-container3">
                    <div className="roomimages-container4" style={{ backgroundImage: `url("${room?.photoConnection[1]?.file}")` }} />
                    <div className="roomimages-container4" style={{ backgroundImage: `url("${room?.photoConnection[2]?.file}")` }} />
                    <div className="roomimages-container4_2" style={ { display: `${_.includes(brightList, 2) ? 'inline' : 'none'}` } } onMouseOver={() => setBrightList([2])}/>
                    <div className="roomimages-container4_3" style={ { display: `${_.includes(brightList, 3) ? 'inline' : 'none'}` } } onMouseOver={() => setBrightList([3])}/>
                </div>
                <div className="roomimages-container3">
                    <div className="roomimages-container4" style={{ backgroundImage: `url("${room?.photoConnection[1]?.file}")` }} />
                    <div className="roomimages-container4" style={{ backgroundImage: `url("${room?.photoConnection[2]?.file}")` }} />
                    <div className="roomimages-container4_2" style={ { display: `${_.includes(brightList, 4) ? 'inline' : 'none'}` } } onMouseOver={() => setBrightList([4])}/>
                    <div className="roomimages-container4_3" style={ { display: `${_.includes(brightList, 5) ? 'inline' : 'none'}` } } onMouseOver={() => setBrightList([5])}/>
                </div>
            </div>
        </div>
    );
}

export default RoomImages;
