import React, { useState, useRef, useCallback, useMemo } from "react";
import { RightOutlined, LeftOutlined } from '@ant-design/icons';
import { Calendar } from 'antd';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Room } from "../generated/graphql";
import '../assets/scss/room.scss';
import * as Moment from 'moment';
import { extendMoment } from 'moment-range';
import _ from "lodash";
let moment = extendMoment(Moment);
moment.locale('ko');
type Props = {
    room: Room
};

const Review: React.FunctionComponent<Props> = ({
    room
}) => {
    console.log('room', JSON.stringify(room));
    return (
        <div style={ { width: '100%', height: '20%', border: '1px solid black' } }>
            <div className="review-container2">
                <div className="review-container1" style={{ backgroundImage: `url('${room?.user?.avatar}')` }} />
                <div className="review-container3">
                    <div><span style={ { fontWeight: 'bold', fontSize: 10 } }>{ room?.reviews[0].user.name }</span></div>
                    <div><span style={ { color: 'gray', fontSize: 10 } }>{ moment(_.replace(room?.reviews[0].createdAt, /"/g, '')).format('YYYY년 MM월') }</span></div>
                </div>
            </div>
        </div>
    );
}

export default Review;
