import React, { useState, useRef, useCallback } from "react";
import ReactSwipe from 'react-swipe';
import { Room } from "../generated/graphql";
import '../assets/scss/room.scss';
type Props = {
    room: Room
};

const RoomdetailPicture: React.FunctionComponent<Props> = ({
    room
}) => {
    let carouselRef: any = useRef();
    return (
        <div>
            <ReactSwipe
                className="carousel"
                swipeOptions={{ continuous: true }}
                ref={el => (carouselRef = el)}
            >
                <div>PANE 1</div>
                <div>PANE 2</div>
                <div>PANE 3</div>
            </ReactSwipe>
            <button onClick={() => carouselRef.next()}>Next</button>
            <button onClick={() => carouselRef.prev()}>Previous</button>
        </div>
    );
}

export default RoomdetailPicture;
