import React, { useState, useRef, useCallback } from "react"
import { Room } from "../generated/graphql";
import 'antd/dist/antd.css';
import { Carousel, Icon } from 'antd';
import '../assets/scss/room.scss';
import Router from 'next/router'
type Props = {
    room: Room
};

const RoomdetailPicture: React.FunctionComponent<Props> = ({
    room
}) => {
    const carouselRef : any = useRef();
    return (
        <div
            className={`w-100 mb-10 px-2 overflow-hidden`}
            style={{ position: 'relative' }}
        >
            <Carousel
                ref={carouselRef}
                autoplay={true}
            >
                {
                    room?.photoConnection.map((photo, index) => (
                        <div key={index}>
                            <div className="w-full h-64 bg-cover bg-center rounded-lg mb-2 " style={{ backgroundImage: `url("${room.photoConnection[index].file}")` }}></div>
                        </div>
                    ))
                }
            </Carousel>
            <div
                style={{
                    borderWidth: 1, borderRadius: 30, width: 25, height: 25, backgroundColor: '#FFFFFF', zIndex: 1, position: 'absolute',
                    top: 108, left: 15, paddingBottom: 8, paddingLeft: 3, display: 'inline', cursor: 'pointer',
                    boxShadow: '1.5px 1.5px 1.5px 1.5px gray'
                }}
            >
                <div style={{ position: 'relative' }}><Icon type="left" style={{ position: 'absolute', top: 5, fontSize: 18 }} /></div>
            </div>
            <div
                style={{
                    borderWidth: 1, borderRadius: 30, width: 25, height: 25, backgroundColor: '#FFFFFF', zIndex: 1, position: 'absolute',
                    top: 108, right: 15, paddingBottom: 8, paddingLeft: 3, display: 'inline', cursor: 'pointer',
                    boxShadow: '1.5px 1.5px 1.5px 1.5px gray'
                }}
            >
                <div style={{ position: 'relative' }}><Icon type="right" style={{ position: 'absolute', top: 5, fontSize: 18 }} /></div>
            </div>
        </div>
    );
}

export default RoomdetailPicture;
